			rs.close();
		}catch (Exception e) {
			e.printStackTrace();
		}
		rs = null;

	}
	if(st!=null){
		try{
			st.close();
		}catch (Exception e) {
			e.printStackTrace();
		}

	}
	if(conn!=null){
